import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setRegisterAction } from '../redux/actions/user.actions';
import { useHistory } from 'react-router-dom';
import { useForm } from "react-hook-form";
import "./styles.css";

function Register() {
    const { register, setRegister } = useForm();
    const dispatch = useDispatch();
    const history = useHistory();

    // const [register, setRegister] = useState({
    //     fullname: "",
    //     email: "",
    //     password: "",
    //     gender: "",
    //     alamat:"",
    //     profesi: ""
    // })

    const isLogged = useSelector((state) => state.user)
    console.log('isloged', isLogged);
    console.log('register', register)

    const handleChange =(event) => {
        // console.log('evenet', event)
        setRegister({
            ...register,
            [event.target.name]: event.target.value,
        });
    }
    const onSubmit = (data) => {
        console.log(data)
    }
    return (
        <>
            {/* <form onSubmit={handleSubmit(onSubmit)}> */}
            <form 
            onSubmit={(event) => {
                dispatch(setRegisterAction(register, event, history))
            }}
            >
                <label>Nama Lengkap</label>
                <input name="fullname" ref={register} onChange={(event) => handleChange(event)} />
                <label>Gender Selection</label>
                <select name="gender" ref={register}>
                    <option value="male">male</option>
                    <option value="female">female</option>
                </select>
                <input type="submit" />
            </form>

        </>
    )
}

export default Register
