import { REGISTER, LOGIN } from "../actions/user.actions";

const token = localStorage.getItem("token");

const initialState = token
  ? {
      isLogged: true,
      data: [],
      loginUser: []
    }
  : {
      isLogged: false,
      data: [],
      registerUser: [],
    };

    const user = (state =  initialState, action) => {
        switch (action.type) {
            case REGISTER:
                return {
                    registerUser: action.payload
                }
            case LOGIN:
                return {
                    loginUser: action.payload
                }
            default:
                return state;
        }
    }

    export default user;
